import React from 'react';
import ReactDOM from 'react-dom';
import Applicant from './applicant';

export default class ApplicantsList extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      applicants: this.props.applicants
    };
    this.updateApplicantsState = this.updateApplicantsState.bind(this);
    this.deleteApplicant = this.deleteApplicant.bind(this);
    this.snd = new Audio("/assets/ring1.wav");
  }  
  
  
  deleteApplicant(applicantId){
    var that = this;
    $.ajax({
      type: 'DELETE',
      url: "/applicants/delete_applicant/"+applicantId
    }).done(function(data){
      that.setState({applicants: data.applicants});
    }.bind(this))
    .fail(function(res){});
  }
  
  updateApplicantsState(applicant) {
    let applicants = [...this.state.applicants];
    let applicantsCopy = applicants.slice();
    let applicantId = applicantsCopy.findIndex((element, index, array) => element.id == applicant.id);
    if (applicantId == -1) {
      applicantsCopy = [applicant].concat(applicants);
    } else {
      applicantsCopy[applicantId] = applicant;
    }
    this.setState( {applicants: applicantsCopy} );
    // var snd = new Audio("data:audio/wav;base64,");  
    this.snd.play();
  }
  
  render(){
    App.candidate = App.cable.subscriptions.create({
        channel: "CandidateChannel", 
      },
      {received: data => {
        if (data.sound)
          this.snd.play();
          // alert("Beep");
        this.updateApplicantsState(data.applicant);
      }
    });
    let applicants = [];
    this.state.applicants.forEach(a => {
      applicants.push(<Applicant key={a.id} applicant={a} onClickDelete={this.deleteApplicant}/>);
    });
    return(
      <div>
        <h1>Телеграммы в буфере</h1>
        <table className="table table-hover">
          <thead>
            <tr>
              <th width = "200px">Дата загрузки</th>
              <th>Тип</th>
              <th>Телеграмма</th>
              <th>Сообщение</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {applicants}
          </tbody>
        </table>
      </div>
    );
  }
}

