$(function () {
    const node = document.getElementById('applicants');
    if (node) {
      const applicants = JSON.parse(node.getAttribute('applicants'));
      
      ReactDOM.render(
        <ApplicantsList applicants={applicants}/>,
        document.getElementById('applicants_list')
      );
    }
  });