import React from 'react';
import ReactDOM from 'react-dom';

export default class ApplicantsList extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      applicants: this.props.applicants
    };
    this.updateApplicantsState = this.updateApplicantsState.bind(this);
  }  
  
  updateApplicantsState(applicant) {
    let applicants = [...this.state.applicants];
    let applicantsCopy = applicants.slice();
    let applicantId = applicantsCopy.findIndex((element, index, array) => element.id == applicant.id);
    if (applicantId == -1) {
      applicantsCopy = [applicant].concat(applicants);
      // applicantsCopy.push(applicant);
    } else {
      applicantsCopy[applicantId] = applicant;
    }
    this.setState( {applicants: applicantsCopy} );
    // var snd = new Audio("data:audio/wav;base64,");  
    // snd.play();
  }
  
  render(){
    const that = this;
    App.candidate = App.cable.subscriptions.create({
      channel: "CandidateChannel", 
      // {      connected: function() {
        // Called when the subscription is ready for use on the server
      },
      // disconnected: function() {
        // Called when the subscription has been terminated by the server
      // },
      {received: function(data) {
        that.updateApplicantsState(data.applicant);
      }
    });
    // App.comments = App.cable.subscriptions.create({
    //     channel: ‘CommentsChannel’,
    //     discussion: `${this.props.threadId}`
    //   }, {
    //     received: function(data) {
    //     propState.updateCommentState(data)
    //   }
    // });
    let applicants = [];
    this.state.applicants.forEach(a => {
      let date = a.created_at.replace(/T/,' ').substr(0, 19)+' UTC';
      applicants.push(<tr key={a.id}><td>{date}</td><td>{a.telegram_type}</td><td>{a.telegram}</td><td>{a.message}</td><td>Изменить</td><td>Удалить</td></tr>);
    });
    return(
      <div>
        <h1>Телеграммы в буфере</h1>
        <table className="table table-hover">
          <thead>
            <tr>
              <th width = "200px">Дата загрузки</th>
              <th>Тип</th>
              <th>Телеграмма</th>
              <th>Сообщение</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {applicants}
          </tbody>
        </table>
      </div>
    );
  }
}

document.addEventListener('turbolinks:load', () => {
  const node = document.getElementById('applicants');
  const applicants = JSON.parse(node.getAttribute('applicants'));
  
  ReactDOM.render(
    <ApplicantsList applicants={applicants}/>,
    document.getElementById('applicants_list')
  );
});